import React from 'react';
import { useParams } from 'react-router-dom';
import MovieList from '../../statics/movielist';

function MovieDetails() {
    const movieData = MovieList
    console.log(movieData)
  const { id } = useParams();
    console.log(id)
  // Convert id to number if needed
  const movieId = parseInt(id, 10);
  console.log(movieId)
  // Retrieve movie details based on movieId
  // For demonstration, let's use a mock movie data array
  const movie = movieData.find((movie) => movie.id === movieId);
  console.log(movie)
  if (!movie) {
    return <div>Loading...</div>; // or display a 404 page
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>{movie.name}</h2>
      <p><strong>Title:</strong> {movie.title}</p>
      <p><strong>Cast:</strong> {movie.cast}</p>
      <p><strong>Category:</strong> {movie.category}</p>
      <p><strong>Budget:</strong> {movie.budget}</p>
      <p><strong>Release Date:</strong> {movie.releaseDate}</p>
      {/* Add more movie details here if needed */}
    </div>
  );
}

export default MovieDetails;
