    releaseDate: '2022-02-15'
  },
  // ... Add more movies with properties
];

function ListComponent() {
  return (
    <div style={{ padding: '20px' }}>
    <Row gutter={16}>
      <List
        grid={{ gutter: 16, column: 3 }}
        dataSource={movieData}
        renderItem={(item) => (
          <List.Item>
            <Card
              title={item.name}
              style={{ 
                width: '100%', 
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Adding drop shadow
              }}
              actions={[
                <Tag color="blue">{item.category}</Tag>,
                <Tag color="green">{item.budget}</Tag>,
              ]}
            >
              <p><strong>Title:</strong> {item.title}</p>
              <Divider />
              <p><strong>Cast:</strong> {item.cast}</p>
              <Divider />
              <p><strong>Release Date:</strong> {item.releaseDate}</p>
            </Card>
          </List.Item>
        )}
      />
    </Row>
  </div>
  );
}

export default ListComponent;
