import React from 'react';
import { List, Card, Row, Col, Tag, Divider } from 'antd';
import { useNavigate } from 'react-router-dom';
import MovieList from '../../../public/movielists';

// Mock movie data with additional properties
const movieData = MovieList

function ListComponent() {
    const navigate = useNavigate();

    const handleCardClick = (movieId) => {
      navigate(`/details/${movieId}`);
    };

    return (
      <div style={{ padding: '20px' }}>
        <Row gutter={16}>
          <List
            grid={{ gutter: 16, column: 3 }}
            dataSource={movieData}
            renderItem={(item) => (
              <List.Item>
                <Card
                  title={item.name}
                  style={{ 
                    width: '100%', 
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                    cursor: 'pointer'  // Change cursor to pointer on hover
                  }}
                  actions={[
                    <Tag color="blue">{item.category}</Tag>,
                    <Tag color="green">{item.budget}</Tag>,
                  ]}
                  onClick={() => handleCardClick(item.id)}  // Navigate to details route
                >
                  <p><strong>Title:</strong> {item.title}</p>
                  <Divider />
                  <p><strong>Cast:</strong> {item.cast}</p>
                  <Divider />
                  <p><strong>Release Date:</strong> {item.releaseDate}</p>
                </Card>
              </List.Item>
            )}
          />
        </Row>
      </div>
    );
  }

  export default ListComponent;
